INSERT INTO `cast` VALUES(153, 'rrice', 'Rosemary', '', 'Rice', 'http://rmt.radio.shows.org/images/rosemary_rice.jpg', '', 'In addition to her success on television as Katrin on the popular MAMA series in the 1950s, Rosemary Rice was a\rbusy radio actress during the 1940s and 1950s and was heard on such radio programs as Studio One, Archie Andrews (as\rBetty), The Cavalcade Of America and When A Girl Marries, to mention just a few. Rice was a regular performer on Himan\rBrown''s CBS Radio Mystery Theater in the 1970s and had numerous successful children''s recordings to her credit\r(imdb.com).', '0000-00-00', '0000-00-00', '', '', 'browncalumet');
